    // }

    //Change Message using the previous state

    changeMessage(){
        this.setState((prevState)=>{this.state.count=prevState+1})
    }

    changeMessageFive(){
        this.changeMessage()
        this.changeMessage()
        this.changeMessage()
        this.changeMessage()
        this.changeMessage()
    }
    render(){
        return(
            <div>
                <h1>{this.state.message}</h1>
                <button onClick={() => this.changeMessageFive()}>Subscribe</button>
        <h2>Subscribers Count : {this.state.count}</h2>
            </div>
        )
    }
}

export default Message;
