     logConsole(){
         const {counterVal} = this.state
         console.log("Counter after callback" + this.state.count.toString)
     }

    //Change Message using the previous state

    changeMessage(){
        this.setState(prevState=>({count:prevState.count+1,message:'Thank You for subscribing',}),()=>this.logConsole())
        console.log("counterValue : " + this.state.count)
    }

    changeMessageFive(){
        this.changeMessage()
        this.changeMessage()
        this.changeMessage()
        this.changeMessage()
        this.changeMessage()
    }
    render(){
        const {counterVal, stateMessage} = this.state
        return(
            <div>
                <h1>{stateMessage}, + initial Subscribers: {counterVal}</h1>
                <button onClick={() => this.changeMessageFive()}>Subscribe</button>
        <h2>Subscribers Count : {counterVal}</h2>
            </div>
        )
    }
}

export default Message;
